function AppDemo() {
    const [toOrder, setToOrder] = useState("")
  const services = [
    {name: "Food", icon: <Chinese /> },
    {name: "Groceries", icon: <Bag />},
    {name: "Courier", icon: <Motorbike />}
  ]
  return (
    <AppScreen>
      <AppScreen.Body>
        <h3 className="bg-secondary p-3 text-center underline">Our Response to Cyclone Freddy</h3>
        <h4 className="mx-4 text-sm mt-6">Hi Takondwa</h4>
        <h4 className="mx-4">{toOrder === "" ? "What would you like to order?" : `Would you like ${toOrder}?`}</h4>
        <section className="flex border-t border-gray-300 mt-6">
          {
            services.map(svc => <div onMouseOver={() => setToOrder(svc.name)} key={svc.name} className="w-1/3 py-4 [&:nth-child(2)]:border-r [&:nth-child(2)]:border-l hover:bg-gray-100 cursor-pointer">
              <div className="w-2/3 mx-auto">
                {svc.icon}
              </div>
              <h5 className="text-center text-sm">{svc.name}</h5>
            </div>)
          } 
        </section>
        <section className="bg-gray-200 p-4">
          <img src="/images/db-discount.png" alt="advert" />
        </section>
      </AppScreen.Body>
    </AppScreen>
  )
}

